(function(_w, $, _d, _dEl) {
	function retrieveGists( url ) {
		$( '.post-body .i18n-' + sessionStorage.getItem( 'lang' ) + ' .jquery-sail-extjs[data-url*="gist.github.com"]' ).each(function() {
			var $el = $( this ).removeClass( 'jquery-sail-extjs' );
			$.ajax({
				url: 		$el.attr( 'data-url' ).replace( /\.js$/, '.json' ),
				dataType: 	'jsonp',
				success:	function( data ) {
					$( 'head' ).append( '<link rel="stylesheet" href="' + data.stylesheet + '" class="blogger-gist">' );
					$el.html( data.div ).removeAttr( 'data-url' );
				}
			});
		});
	}
	function reloadDisqus() {
		var $comments = $( '#comments' );
		if ( ! $comments.length ) {
			return false;
		}
		_w.disqus_shortname = 'grenouille220';
		_w.disqus_blogger_current_url = $comments.attr( 'data-disqus_blogger_current_url' );
		_w.disqus_blogger_homepage_url = $comments.attr( 'data-disqus_blogger_homepage_url' );
		_w.disqus_blogger_canonical_homepage_url = $comments.attr( 'data-disqus_blogger_canonical_homepage_url' );
		_w.disqus_url = _w.disqus_blogger_current_url;
		if (typeof DISQUS === "undefined") {
			(function() {
				var bloggerjs = document.createElement( 'script' );
				bloggerjs.type = 'text/javascript';
				bloggerjs.async = true;
				bloggerjs.src = '//' + disqus_shortname + '.disqus.com/blogger_item.js';
				(document.getElementsByTagName('head')[0] || document.getElementsByTagName('body')[0]).appendChild(bloggerjs);
			})();
		}
		else {
			$( '#comments' ).html( '<div id="disqus_thread"></div>' );
			DISQUS.reset({
				reload: true,
				config: function () {
					this.page.url = disqus_url;
				}
			});
		}
	}
	function adjustHead() {
		var lang = sessionStorage.getItem( 'lang' );
		$( 'meta[property="og:url"]' ).each(function() {
			if ( lang !== 'en' && ! /\?/.test( $( this ).attr( 'content' ) ) ) {
				$( this ).attr( 'content', $( this ).attr( 'content' ) + '?lang=' + lang ); 
			}
			else if ( lang === 'en' && /\?/.test( $( this ).attr( 'content' ) ) ) {
				$( this ).attr( 'content', $( this ).attr( 'content' ).replace( /\?lang\=\w+/, '' ) );
			}
		});
	}
	function i18n() {
		if ( ! _w || ! _w.sessionStorage || ! _d.getElementsByClassName ) {
			return false;
		}
		var $header = $( '.description' ),
			$menu = $( '<span id="Lang3"></span>' ),
			lang = sessionStorage.getItem( 'lang' ) || 'en';
		if ( /\?lang\=/.test( _w.location.search ) ) {
			lang = _w.location.search.match( /\?lang\=(\w+)/ )[ 1 ];
			window.location.search = '';
		}
		else if ( ! sessionStorage.getItem( 'lang' ) && _d.referrer && ! /1\.grenouille220\.com/.test( _d.referrer ) ) {
			var navLang = _w.navigator.userLanguage || _w.navigator.language;
			if ( /^fr/.test( navLang ) ) {
				lang = 'fr';
			}
			else if ( /^ja/.test( navLang ) ) {
				lang = 'ja';
			}
			else {
				lang = 'en';
			}
		}
		if ( ! $header.length ) {
			return false;
		}
		var switchLang = function( newLang ) {
			lang = newLang;
			sessionStorage.setItem( 'lang', lang );
			_d.documentElement.className = _d.documentElement.className.replace( /i18n-\w+/, 'i18n-' + lang );
			retrieveGists();
			adjustHead();
		};
		switchLang( lang );
		$menu.html([
			'<a href="#en" class="' + ( lang === 'en' ? 'selected' : '' ) + '">English</a>, ',
			'<a href="#fr" class="' + ( lang === 'fr' ? 'selected' : '' ) + '">Français</a> or ',
			'<a href="#ja" class="' + ( lang === 'ja' ? 'selected' : '' ) + '">日本語</a> ok'
		].join( '' ));
		$header.append( $menu );
		var $curr = $menu.find( 'a.selected' );
		$menu.on( 'click', 'a', function( event ) {
			event.preventDefault();
			if ( ! $( this ).hasClass( 'selected' ) ) {
				$curr.removeClass( 'selected' );
				$curr = $( this ).addClass( 'selected' );
				switchLang( $( this ).attr( 'href' ).toString().replace( /.*?#/, '' ).substring( 0, 2 ) );
			}
		});
    }
	$( i18n );
	function setLayout( $root ) {
		$root.find('.post .screenshots img:not(.icon-action)').parents('a').attr('data-lightbox', 'gallery');
		var lightBoxOptions = {};
		lightBoxOptions.disableScrolling = true;
		lightBoxOptions.resizeDuration = 250;
		lightBoxOptions.imageFadeDuration = 250;
		lightBoxOptions.fitImagesInViewport = true;
		var firstImg = $root.find( 'a[data-lightbox]:eq(1) img' );
		if ( firstImg && firstImg.length && ( firstImg.attr( 'width' ) + 1 ) < firstImg.attr( 'height' ) + 0 ) {
			lightBoxOptions.fitImagesInViewport = false;
		}
		if ( _dEl && _dEl.lang ) {
			if ( _dEl.lang === 'fr' ) {
				lightBoxOptions.albumLabel = 'Image %1 de %2';
			}
			if ( _dEl.lang === 'ja' ) {
				lightBoxOptions.albumLabel = '画像 %1/%2';
			}
		}
		lightbox.option( lightBoxOptions );
		return $root;
	}
	$( _w ).on( 'load', function() {
		if ( $( _w ).width() < 900 ) {
			return true;
		}
		$( window ).sail({
			area: [ '#main' ],
			pjaxUpdateHeadBefore: function() {
				$( 'head link.blogger-gist' ).remove();
			},
			pjaxUpdateContentAfter: function( $el, lazyloader ) {
				retrieveGists();
				reloadDisqus();
				return setLayout( $el );
			}
		});
		setLayout( $( 'body' ) );
		$( _d ).on( 'mousedown', '#lightbox', function() {
			setTimeout(function() {
				$( '#lightbox' )[ 0 ].scrollTop = 0;
			}, 200);
		});
	});
})(window, window.jQuery || false, document, document.documentElement);
//
